/**
 * @name about:reader Dark
 * @stylish-id 119673
 * @author Niklas Gollenstede
 * @licence CC-BY-SA-4.0 or MIT
 * @description
 *     Makes the reader view of firefox even darker :)
 *
 *     If you have any issues with this style, please open a ticket at https://github.com/NiklasGollenstede/stylish
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("about:reader") {
    body
    {
        background-color: #14171A !important;
        color: #EEEEEE !important;
    }

    #reader-toolbar
    {
        background-color: #343C45 !important;
        border-color: black !important;
    }
    #reader-toolbar li
    {
        filter: invert(100%) hue-rotate(180deg);
        background-color: #BDC5CE !important; /* inspector header color filtered */
    }
    .style-button:not(:hover),
    #close-button:not(:hover)
    {
        border-color: white !important;
        background-color: #BDC5CE !important; /* inspector header color filtered */
    }

    #color-scheme-buttons
    {
        display: none !important;
    }
}
