css:
@-moz-document domain("fitloop.co") {


/*Universal*/
body {background-color:#161616;}
h1 {color:silver;} 
h2 {color:silver;}
h3 {color:silver;}
h4 {color:silver;}
h5 {color:silver;}
p {color:silver;}

/*Front*/
#page {
	background-color:black;
	box-shadow:none;
}

/*Routine*/
div.detail-panel {background-color:#161616;}
div.routine {background-color: black;}
ul.list-group {background-color: black;}
li {color:silver;}
.notes {color:silver;}
span.exercise-name {color:silver !important;}
.exercise-item {background-color:#403B73 !important;}
}
