css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hardwareluxx.de") {
  iframe[id^="google_ads_iframe"], .cc_banner.cc_container.cc_container--open, [style="width: 300px; height: 250px;"],
  BODY > #center > [style="position:relative"] + #wrapper > #top + DIV, ARTICLE.cf DIV > IMG:first-child,
  #wrapper > #top + DIV + #oben + .cf + DIV,
  SECTION > [style="border-bottom: 2px #E4E4E4 solid;"] + DIV,
  SECTION > H3[style="border-bottom: 2px #E4E4E4 solid;"]:first-child + DIV,
  H3[style="border-bottom: 2px #E4E4E4 solid;"] {display: none !important;} 
}
