css:
@-moz-document domain("www.worldofanime.com") {
.menu_core_main, .navigation, .layout_page_header  {background-color:#57c5db!important;}

.layout_core_menu_main, li {border:0px  !important;}

#global_wrapper, #core_menu_mini_menu{background-color:#57c5db !important;border:0px !important;} 

#global_footer {border:0px !important;}

#updates_toggle, .new_updates  {background-color:#57c5db !important;}

#core_menu_mini_menu_update {background-color:transparent !important;}

html {background-color:#ffffff !important; opacity:0.8 !important;}

#global_content, .generic_layout_container, .layout_page_user_index_home, .layout_main, .layout_left, .layout_right, .layout_middle, .feed_item_body, h3 {background:#ffffff !important; background-color:#ffffff !important;  background-image:none !important;}

a:link, a:visited {color:#2d2d2d !important;}

a:hover {color:#6d6d6d !important;}

.menu_core_main  {color:#ffffff;}

.headline, .tabs > ul li>a  {background-color:#ffffff !important;}
.quicklinks {background-color:#ffffff !important;}


}
