css:
@-moz-document domain("www.keyhero.com") {
body {
    background-color: #2C2C2C !important;
    color: #ccc;
}
.description-block {
    background-color: #2C2C2C !important;
    color: white !important;
}
tr.head td {
    background-color: #2C2C2C !important;
    color: white !important;
}
div.new-post a {
    background-color: #2C2C2C !important;
    color: white !important;
}
.quote-current {
    background: black;
}
.quote-left {
    color: black;
}
.navbar-default .navbar-collapse,
.navbar-default .navbar-form {
    background: #202020 !important;
}
.navbar-default {
    background-color: #202020 !important;
    box-shadow: none !important;
}
h4,
.h4 {
    color: #ccc !important;
}
span {
    color: #A2A2A2 !important;
}
.dl-horizontal dd {
    background-color: #2c2c2c !important;
}
.dl-horizontal dt {
    color: #808080 !important;
}
.table-striped>tbody>tr:nth-child(odd)>td,
.table-striped>tbody>tr:nth-child(odd)>th {
    background: #2c2c2c !important;
}
.row {
    background: #2c2c2c !important;
}
footer {
    background: #2c2c2c !important;
}
h3,
.h3 {
    color: #ccc !important;
}
.alert-info {
    background: #3c3c3c !important;
}
.form-control {
    background: #202020 !important;
    color: #ccc !important;
    border: none !important;
}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    background: #3c3c3c !important;
}
input, button, select {
    background: #2c2c2c !important;
}
textarea {
	background: #3c3c3c !important;
}
.panel-body {
    background: #3c3c3c !important;
}
.panel-default>.panel-heading {
    background: #3c3c3c !important;
}
.quote-bg-error {
    background: #4c4c4c !important;
}
.correction {
    background: #2c2c2c !important;
}
.label-awesome {
    background-color: #84840B !important;
}
#tooltip {
    background-color: #202020 !important;
}
.legendLabel {
    background: #202020 !important;
    color: #ccc !important;
}
.btn-success {
    background: #21692B !important;
    border-color: #21692B !important;
}
.btn-info {
	background: #772953 !important;
}
.well {
 	background: #202020;   
}
.navbar-default .navbar-nav>li>a {
	color: #ccc !important;
}
.media, .media-body {
	background: #262626 !important;
	border-radius: 5px !important;
}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
	background: #3c3c3c !important;
}
.topic-message {
	border: none !important;
	border-radius: 5px !important;
	background: #202020 !important;
}
.pager li>a, .pager li>span {
	background: #202020 !important;
	border: 1px solid #313131 !important;
}
.btn-link {
	background: #242424 !important;
	border-radius: 5px;
}
.btn-primary {
	background: #dd4814 !important;
}
.comment-reply {
	background: #2c2c2c !important;
	border-radius: 3px !important;
}
.btn-default {
	border-color: #202020 !important;
}
.parent-message {
	background: #2c2c2c !important;
}
.alert-warning {
	background: #3c3c3c !important;
	border-color: #a9a9a9 !important;
}
.label-great {
    background: #8E4D00;
}

/* vimmium styles */
div.internalVimiumHintMarker {
 	border: black !important;
	background: rgb(78, 146, 255) !important;
}
div.internalVimiumHintMarker span {
    color: black !important;
    text-shadow: none !important;
}
div.internalVimiumHintMarker {
	color: red !important;
}
div.internalVimiumHintMarker > .matchingCharacter {
	color: white !important;
}
/* 14.11.2015 12:35 */
}
