css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habitica.com") {

    .header-wrap {
        background: url('http://sel.shizuyue.net/img/mine/rpg-bg.png');
        background-size: cover !important;
    }

    .herobox {
        background-color: transparent !important;
    }

    .site-header .main-herobox {
        background: rgba(255, 255, 255, .5);
    }

    .site-header .main-herobox:hover {
        background: rgba(255, 255, 255, .7);
    }
    
    .site-header  div.hero-stats {
        background: rgba(207,219,222, .7);
    }


    .container-fluid ul.nav li a {
        padding: 5px 10px 5px 10px !important;
    }
    
    .task-column {
        border: 0px !important;
        border-radius: 10px !important;
    }

    .task-text p {
        margin-top: 12px !important;
        min-height: 25px !important;
    }

    .task-text img {
        margin: -15px 5px 1px -2px !important;
        float: left !important;
        width: 38px !important;
    }

    .task-text span {
        background-color: red !important;
    }

    .task-meta-controls {
        margin: 3px 5px -5px 0px !important;
    }

    .task-meta-controls span, .task-meta-controls a, div.task-meta-controls .glyphicon {
         font-size: 10px !important;
    }

    .task-meta-controls .badge {
        padding: 2px 4px 2px 4px !important;
        border-radius: 5px !important;
    }

    div.task-meta-controls .glyphicon {
        margin: 0px !important;
        letter-spacing: 2px !important;
    }
}
