css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
/* Make menu buttons teal */

.mainmenu .menugroup p .button {
    background: linear-gradient(to bottom, hsl(180, 100%, 25%), hsl(180, 100%, 25%)) !important;
    border-color: hsl(210, 40%, 40%) !important;
}
.mainmenu .menugroup p .button:hover {
    background: linear-gradient(to bottom, hsl(180, 100%, 36%), hsl(180, 100%, 25%)) !important;
}
.mainmenu .menugroup p .button:active {
    background: linear-gradient(to bottom, hsl(180, 100%, 10%), hsl(180, 100%, 25%)) !important;
}
}
