@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tp.zp.ua") {
  @media (min-width: 1200px) {
    .mynav_navbar-default {
      height: 320px;
    }
    
    .new_menu_bottom_left > ul > li > a {
      margin: 0px 18px !important;
    }
    
    .new_menu_bottom_left > ul > li > a:before, .new_menu_bottom_left > ul > li > a:first-child *::before {
      left: 50% !important;;
      margin-left: -36px !important;;
    }

    #menu-item-373 > a:nth-child(1) {
      padding-left: 13px;
    }

    #menu-item-312 > a:nth-child(1) {
      padding-left: 19px;
    }

    #menu-item-312, #menu-item-161 {
      margin-left: -10px !important;
    }
  }
}
