css:
@-moz-document domain("reddit.com") {
/* Text */
.link .score.likes {
    color: #85BF25 !important;
}
.link .score.dislikes {
    color: #EE4444 !important;
}

/* Arrows */
.arrow.upmod {
	-webkit-filter: hue-rotate(460deg);
	filter: hue-rotate(90deg);
}
.arrow.downmod {
	-webkit-filter: hue-rotate(460deg);
	filter: hue-rotate(460deg);
}
}
