css:
@-moz-document domain("kwick.de") {
/* HTML-Farbcodes: http://html-color-codes.info */
/* #FFFFFF = Weiß | #000000 = Schwarz */

body {
    background-color: #EBEFDA !important;
    /* Hintergrundfarbe der Seite*/
}
.container.containerLegacy #contentcontainer,
#module_Controller_Module_Profile_AboutMe > div > div.commentLeft > div.socialObjectInitializer > social-social-object > div > .actionsContainer,
#eventDetail > div > div.commentLeft > div.socialObjectInitializer > social-social-object > div > .actionsContainer {
    background-color: #fff !important;
    /* Hintergrundfarbe des Content-Bereiches */
}
#tinymce {
    background-color: #FFFFFF !important;
    /* Hintergrundfarbe des Textfeldes im Texteditor */
}
.colRight h3 {
    background-color: #FFFFFF !important;
    /* Überschriftenfarbe in der rechten Spalte im Profil */
}
table td.action,
.messageDialog,
.userLine.userLineHighlight {
    background-color: #EAF1C6 !important;
    /* Hintergrundfarbe im Message-Dialog, Action-Spalte in Message-Ünbersicht und Ereignis-Dialog */
}
.socialObject .interactionContainer,
.socialObject .storyContainer,
.socialObject .actionsContainer,
.slideModule .contentBox {
    background-color: #FFFFFF !important;
    /* Hintergrundfarbe von Neuigeiten und Slidebox */
}
#forum td.post,
#forum td.post .socialObject .actionsContainer {
    background-color: #FFFFFF !important;
    /* Hintergrundfarbe von Forumsbeiträgen */
}
.container.containerLegacy #contentcontainer.contentBoxAlternate,
.contentBox.contentBoxAlternate,
.zappingContainer,
html h3,
.shoutBox.shoutBoxAlternate,
.uiFullBoxBackground,
table.forum thead tr,
.paginator > a.currentPage,
.paginator > a.currentPage:active,
#forum td.author,
#forum th,
.boxLink,
.uiWidget,
.vCard .vCardHeader,
.vCard .vCardFooter,
.modal .modalHeader,
.modal .modalFooter,
.modal .modalSeparator,
.selector,
.clearlooks2 .mceTop,
table th,
.window .head,
.colOne.notifications table .th {
    background-color: #DFF08B !important;
    /* Hintergrundfarbe von Kommentarbereichen, Tabellenköpfen, Überschriften, etc */
}
}
