css:
@-moz-document domain('pagingdr.net')
{
   .windowbg, .windowbg2 { padding: 25px 5px 25px 5px; }
   .windowbg span.topslice span, .windowbg2 span.topslice span { display: none; }
   .windowbg span.botslice span, .windowbg2 span.botslice span { display: none; }

   #forumposts .reportlinks, forumposts .signature, #block9 { display: none; }
   p#whoisviewing, #tpcenterbarHeader, #top_section { display: none; }
   #tprightbarContainer, #tplowerbarHeader, #moderationbuttons, #footer_section { display: none; }
   img[alt='Modify message'] { display: none; }

   #content_section { margin-bottom: 20px; }
   
   #quickReplyOptions form textarea { height: 250px; }
}
