css:
@-moz-document domain("kahoot.it") {
*{ 
    color: #FFF !important;
   background-color: rgb(23, 23, 23)  !important;
    
}

.resultbar{
    background-color: #fff !important;
}
  
/*                v.ʕʘ‿ʘʔ.v        by alpha delta      	*/

.resultbar {
    background-color: #8A8A8A !important;
}
.controls {
    margin-top: 20px;
    text-align: right;
    background: transparent !important;
}

.ranking-screen .spinner, .answerFeedback .spinner {
    -webkit-animation: counter 5s ease-out infinite;
    -moz-animation: counter 5s ease-out infinite;
    -o-animation: counter 5s ease-out infinite;
    -ms-animation: counter 5s ease-out infinite;
    animation: counter 5s ease-out infinite;
   
    width: 70px;
    height: 70px;
    margin: 0 auto;
} 
.join_view {
    overflow: hidden;
    height: 100%;
    background-color: #1D1D1D;
    animation: bgcolor 0s infinite;
    -moz-animation: bgcolor 0s infinite;
    -webkit-animation: bgcolor 0s infinite;
    -o-animation: bgcolor 0s infinite;
    animation-direction: alternate;
    -webkit-animation-direction: alternate;
    -o-animation-direction: alternate;
    -moz-animation-direction: alternate;
}

}
