css:
@-moz-document url-prefix("https://www.youtube.com/") {
#masthead-positioner { position: relative; }
html, body,
#body-container { background-color: #222; padding: 0; height: auto!important;}

#theater-background { background-color: #141414!important; }

#yt-masthead-container { background: none; border: 0; }
#a11y-announcements-container { top: 50px; margin-left: 10px;}

#masthead-search-terms, #masthead-search-term { background: none; color: #999; box-shadow: none; border: 0; }
#search-btn { width: auto; background: none; border: 0; }
#search-btn span { margin: 0 10px!important; }
#masthead-search-term { background: rgba(255,255,255,0.05)!important; }

#masthead-positioner-height-offset, 
#yt-masthead-signin { display: none; }

#watch7-sidebar-discussion,
#watch-discussion { display: none; }
#action-panel-details { max-height: none; overflow: auto; background: none; box-shadow: none; }
#watch-description-clip { color: #888; }
#watch-description-clip a { color: #96f; }
#action-panel-details button { display: none; }
#action-panel-details,
#watch-description-text { height: auto!important; max-height: none!important; overflow: auto; }

#watch8-action-buttons { border-top: 1px solid #333;}

.yt-uix-expander .yt-uix-expander-collapsed-body, 
.yt-uix-expander-collapsed .yt-uix-expander-body{display:block}

#watch7-sidebar-ads { display: none; height: 0!important; } 

#watch-related { height: 400px; overflow: auto; }

#watch7-sidebar-contents { background: none; box-shadow: none; }

#watch7-user-header a,
#watch7-sidebar-modules span.title,
#watch7-sidebar-modules h4 { color: #999!important; }

#logo-container { display: none!important; }

#watch-header { background: none; box-shadow: none; }
#eow-title { color: #999; }


#watch8-action-buttons span { color: #999; }


#footer-container { display: none; }


/* Frontpage */

#video-masthead { display: none; }

#appbar-guide-menu { background: none; color: #666; font-size: 10px;}

#guide-container span.display-name { color: #999; }

#appbar-guide-menu { border: 0!important;}


.branded-page-box,
#content div ol.item-section,
hr.guide-section-separator { border-bottom: 1px solid #333!important; }

#content div { background: none; }

#content div a { background: none; }

h3.yt-lockup-title a, 
span.branded-page-module-title-text { color: #999; }

h2.branded-page-module-title a,
span.yt-badge,
span.yt-uix-button-subscription-container button,
span.yt-uix-button span,
span.yt-uix-button-content,
span.yt-subscription-button-subscriber-count-branded-horizontal,
span.yt-subscriber-count,
div.compact-shelf button { background: none!important; border: 0!important; box-shadow: none; color: #999; }

span.yt-subscriber-count { border: none; }

span.yt-uix-button-content { color: #666; }

button.load-more-button { background: none; border: 0; }

#gh-banner { display: none; }

span.qualified-channel-title-text a { color: #999!important; }

button.yt.uix-button, 
span.yt-uix-button-subscription-container button { background: none; }

#channel-navigation-menu h2 { color: #999; }

span.video-thumb, span.yt-thumb { background: none; }

li.signup-promo { display: none; }

#ad_creative_1 { display: none; }

#results div.pyv-afc-ads-container { display: none; }
}
