css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
  .athing span { color: black !important; }
  
  body {
    margin: 0 !important;
    padding: 0 !important;
    font-size: 3vw !important;
  }
  
  .comhead,
  .title {
    font-size: 3vw !important;
  }
  
  .subtext {
    font-size: 2.25vw !important;
  }
  
  form {
    display: none;
  }
  
  table {
    width: 100%;
  }
  
  table + br,
  table + br + br {
    display: none;
  }
  
  #hnmain > tbody > tr:first-child,
  #hnmain > tbody > tr:last-child,
  #hnmain > tbody > tr:first-child + tr {
    display: none;
  }
  
  .comment {
    font-size: 3.75vw !important;;
  }
  
  .reply {
    display: none;
  }
  
  #hnmain { width: 100% !important; }
  
  tr { position: relative; }
  
  .ind img { width: 40px; }
  .ind img[width="0"] {
    position: absolute;
    width: 100%;
    height: 100%;
    padding-bottom: 5px;
    top: 0;
    background-color: rgba(0, 0, 0, 0.1);
  }
}
