css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nodejs.org") {
  body {
    background: black;
  }
  #column2 ul, pre {
    background: black;
  }
  #column1.interior {
    border-color: transparent;
  }
  body {
    color: lightgrey;
  }
  h1, h2, h3, h4, h5, h6 {
    color: grey;
  }
  p tt, p code, li code {
    color: violet;
  }
  span.type {
    color: green;
  }
  .sh_sourceCode .sh_keyword {
    color: green;
  }
  .sh_sourceCode .sh_symbol, .sh_sourceCode .sh_cbracket {
    color: lightgrey;
  }
  .sh_sourceCode .sh_string, .sh_sourceCode .sh_regexp, .sh_sourceCode .sh_number, .sh_sourceCode .sh_specialchar {
    color: lightpink;
  }
  #column2 ul li a.active {
    color: cyan;
    border: none;
  }
  a {
    color: lightpink;
  }
}
