css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wowhead.com") {
.footer-social,.comment-tabs-block > div.block-bg:nth-child(1),.vertical-bg,a.header-expandsite,.blocks,#news-block-group, .announcement-inner,.premium-promo-row {
display:none!important;
}

.news-right > div:nth-child(1) {
margin-bottom:0!important;
}

.layout-inner {
padding-left:10px!important;
padding-right:10px!important;
}

footer .footer-zam > div {
width:33%;
}

footer .footer-zam > div.block-content:nth-child(3) {
width:34%;
}

.wrapper,.comment-tabs,.comment-tabs-body,.header .header-right {
margin-right:0 !important;
}

.page-content {padding-right: 0px !important;}
.sidebar {padding: 0px !important;}

}
