css:
@-moz-document domain("facebook.com") {

/* remove right col, expand center */

#rightCol {
    display: none !important
}
.sidebarMode #stream_pagelet,
#pagelet_group_,
#pagelet_group_permalink {
    margin-left: 320px !important;
    min-width: 170% !important
}
.mtm > div[data-ft^="{"] > a,
.mtm > div[data-ft^="{"] > a img {
    min-width: 100% !important;
}
#pagelet_side_ads {
    display: none !important
}
/*Left-col unclickable fix*/

#leftCol {
    z-index: 2 !important
}
/* expand chatbox */

.fbNub {
    width: 345px !important;
    margin-left: 3px !important;
}
.fbDockChatTabFlyout {
    width: 100% !important;
}
.fbNubFlyout > div > div > div:nth-child(2) {
    height: 450px !important
}
.fbNubFlyoutBody {
	height: 100% !important;
    max-height: 100% !important;
}
.fbNubFlyoutBodyContent .uiScrollableArea {
	width: 100% !important
}
}
