    padding: 0px 8px;
    margin: 0px auto;
    max-width: 960px;
    width: 100%;
}
   #qrzleft, .qadv, .bq, #qrzcenter > div:nth-child(3) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    display: none;
  }
  #qrzcenter > div:nth-child(3) {
    margin: 0 !important;
  }
  #qrzfooter {
    background: url(https://8932c554b4f0bf30404d-ba90e96e7aaeb84d7874db7cdcba96a2.ssl.cf1.rackcdn.com/waveform.jpg) repeat-x !important;
    height: 150px !important;
    padding: 0 !important;
  }

  @media (min-width: 768px) {
    #biodata {
      font-size: 32px !important;
      line-height: 1.3 !important;
    }
  }
