css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.bing.' ) , url-prefix('https://www.bing.' ) , url-prefix('http://news.bing.' ) , url-prefix('https://news.bing.' ) , url-prefix('http://bing.' ) , url-prefix('https://bing.' ) , url-prefix('https://4.hidemyass.com' ) , url-prefix('https://7.hidemyass.com' )

{

/*
Singularity Utopia Style
@2045singularity
7 Nov 2015
*/

  
/*  

  #evpxl.hlig, html, body 
 
  {
    max-width: 100% !important;
    width: 100% !important;
    min-width: 100% !important;
  }

*/  
 
#sb_form
  
  {
    max-width: 99.9% !important;
    width: 99.9% !important;
    min-width: 99.9% !important;
    padding-right: 0px !important;
    margin-right: 0px !important;
  }

body, html
  
    {
    max-width: 100% !important;
    width: 100% !important;
    min-width: 100% !important;
    padding-right: 0px !important;
    margin-right: 0px !important;
  }

  
#VerpContent, #SerpResultCover, .VerpAjaxRefreshArea, .Content,  #VerpContent.en.en-GB
  
  {
    max-width: 90% !important;
    width: 90% !important;
    min-width: 90% !important;
  }
  
  
#b_notificationContainer .psacontent
  
  {
    display: none !important;
  }
  
#b_results > li a, #b_context a
  
  {
    color: #1A0DAB !important;
  }  
  
h2 strong 
  
  {
    font-weight: normal !important;
  }  
  
.rms_img
  
  {
    border-radius: 2px;
  }
  
#b_header
  
  {
    border-bottom-color: lightgrey;
  }
  
.b_topbar + #sb_form  
  {
    background: #EAEAEA;
    border: 1px solid lightgrey;
    box-shadow:  inset white 0px 0px 1px 0px;
    max-width: 100% !important;
  }

/* NEWS */  
  
.ans_news .b_topTitle
  
  {
    background: #FFF4C7 !important;
    border: 1px solid #EBD8C0 !important;
    text-decoration: none !important;
  }
  
.ans_news .b_topTitle:hover
  
  {
    border-style: dashed !important;
  }
  
/* IMAGES */  
  
.b_topTitle
  
  {
    border: 1px solid #9ADBA9 !important;
    border-radius: 14px 3px 2px 2px; 
    background: #D6FFE0 !important;
  }  

.b_topTitle:hover
  
  {
    border-style: dashed !important;
  }
  
.Content a 
  
  {
    font-family: arial !important;
    text-decoration: none;
    color: #1A0DAB !important;
  }
  
/* MAIN RESULTS */  
  
#b_results h2, .newstitle
  
  {
    background:#DFE2ED;
    padding: 7px 6px 8px 10px;
    border: 1px solid #C9CAD6;
    margin-bottom: 2px;
    border-radius: 14px 3px 3px 3px;
    font-family: arial;
    font-size: 14pt !important;
    font-weight: normal !important;
    text-decoration: none !important;
    box-shadow:  inset white 0px 0px 1px;
  }

#b_results h2:hover, .newstitle:hover
  
  {
    border: 1px dashed #C9CAD6;
  }

.RightRail .actions
  
  {
    width: 142px;
    max-width:100% !important;
    display: inline-block !important;
    position:fixed !important;
    margin: 0px !important;
    
  }

#RightRailResult.RightRail
  
  {
    width: auto;
  }
  
/* KNOWLEDGE GRAPH  #FFF9E6 */   
  
#b_context .b_ans, .b_expando .b_ans, ul.actions
  
  {
    margin-top: 9px;
    padding: 18px 20px 13px;
    background: #F0F0F0 !important;
    border-radius: 18px 4px 4px 4px;
    border: 1px solid lightgrey;
    box-shadow: grey 0px 10px 20px -22px, inset white 0px 0px 1px;
  }

ol#b_context
  
  {
    width: 258px;
  }  
  
#irpserp
  
  {
    margin: 3px 0px 5px 0px;
  }
}
