css:
@charset "UTF-8";
/* Works with the Stylish add-on, https://userstyles.org/help/stylish */
@-moz-document domain(twitter.com) {
.HeartAnimationContainer {
  visibility: hidden
}
.HeartAnimationContainer:after {
  content:'?';
  font-size: 110%;
  visibility: visible;
  display: inline-block;
}
.favorited .IconTextContainer, .favorited .HeartAnimationContainer:after, .ProfileTweet-action--favorite.withHeartIcon .Icon--heartBadge:before, .Icon--heartBadge:before {
  content:'?';
  font-size: 110%;
  visibility: visible;
  display: inline-block;
}
.Icon--heartBadge:before {
  position:absolute;
  bottom:0;
}

}
