css:
@-moz-document url-prefix("https://twitter.com/"), url-prefix("https://api.twitter.com"), url-prefix("https://amp.twimg.com") {
.HeartAnimationContainer:before {
    content: "\f147";
    display: block;
    font-family: "rosettaicons";
    font-weight: normal;
    font-style: normal;
    text-align: center;
    -webkit-font-smoothing: antialiased;
}
.ProfileTweet-action--favorite.withHeartIcon.is-animating .HeartAnimation,
.HeartAnimation {
    display: none!important;
}
.tweet-action[rel="favorite"]:hover,
.tweet-detail-action[rel="favorite"]:hover,
.tweet-action[rel="favorite"]:focus,
.tweet-detail-action[rel="favorite"]:focus,
.tweet-action[rel="favorite"]:active,
.tweet-detail-action[rel="favorite"]:active,
.tweet-action.is-selected[rel="favorite"],
.Icon--favorited,
.Icon--colorFavorited,
.ProfileTweet-action--favorite .ProfileTweet-actionButton:hover *,
.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus,
.ProfileTweet-action--favorite .ProfileTweet-actionCount:hover,
.ProfileTweet-action--favorite .ProfileTweet-actionCount:focus,
.tweet.favorited .tweet-actions .Icon--favorite,
.tweet.favorited .ProfileTweet-actionList .Icon--favorite,
.tweet.favorited .ProfileTweet-action--favorite .ProfileTweet-actionCount,
.Icon--colorHeart,
.Icon--circleActiveHeart,
.Icon--heartBadge,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:hover,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButton:focus,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:hover,
.ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionCount:focus,
.favorited .ProfileTweet-action--favorite .Icon--heart,
.favorited .ProfileTweet-action--favorite.withHeartIcon .ProfileTweet-actionButtonUndo {
    color: #FFA200!important;
}

.hearty .icon-favorite:before {
    content: "\f007"!important;
}
.Icon--heartBadge:before {
    content: "\f001"!important;
}
.prf [data-type="favorites"]:hover *,
.tweet-action[rel="favorite"]:hover,
.tweet-detail-action[rel="favorite"]:hover,
.tweet-action[rel="favorite"]:focus,
.tweet-detail-action[rel="favorite"]:focus,
.tweet-action[rel="favorite"]:active,
.tweet-detail-action[rel="favorite"]:active,
.tweet-action[rel="favorite"].is-selected,
.is-favorite .icon-favorite-toggle,
.hearty .icon-favorite-color {
    color: #FFA200!important;
}

}
