css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("mediapart.fr") {
  body {
    font-family: Open_Sans_Regular,Arial,Verdana,sans-serif;
  }

  div::before {
    border: none;
  }
  
  .portfolio-list {
    background-color: black;
    color: white;
    padding: 30px;
  }
  
  .legend {
    color: white;
  }
  
  .portfolio-list li .legend .context p {
    color: white;
  }

  .title {
    font-family:   PT_Serif,"Times New Roman",Times,serif;
  }

  #colClub {
    width: 0;
    display: none;
  }

  .fractal-10-desktop.collapse-7-desktop {
    width: 100%;
  }
}
