@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("twitter.com") {
 
    .HeartAnimationContainer {
        visibility: hidden;
    }
 
    .HeartAnimationContainer:after {
        visibility: visible;
        display: block;
        position: absolute;
        top: -4px;
        font-size: 23px;
        content: '♣️';
    }
}
