css:
@-moz-document domain("mysku.ru") {

#header, #sidebar, .coupon, .wrap, .category-list, .read_all, .profile-blog, .additional-materials-list, .login_in_comments, #footer
{
  display: none;
}
  
#content, .topic, .container, .comment_text
{
  padding: 1ex !important;;
  margin: 0 !important;
  width:100% !important;;
  color: white !important;
  background-color: black !important;
  min-width: 1500px;
}
  
#content a {
  color: #40D0F0;
}

#wrapper
{
  margin: 0 !important;
  padding: 0 !important;
  color: white !important;
  background-color: black !important;
}
  
.total {
  color: black;
}
  
#pagination.paginator a{
  color: #008080;
}
  
.author {
  background: white;
}
  
.product_image,
.topic,
.content  {
  border: none !important;
  margin: 1ex !important;;
}
  
.spoiler-title {
  padding: 0 !important;
  margin: 0 !important;
  color: black !important;
  background: #E08000 !important;
}
  
.spoiler-body {
  padding: 0 !important;
  margin: 0 !important;
  color: white !important;
  background: #202020 !important;
}

}
