css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('http://tmv\\.proto\\.jp/(?!tmv2.0).*')
  
{
  
  #Header
    {background: linear-gradient(rgba(25, 25, 25, 1), rgba(25, 25, 25, 0.7))}
  #Menu, html, body, #dashboard, #likes, #HomeAround, #list, #around, #newest, #random, #oldest, #tag, #dashboard > UL, #list > UL, #HomeAround > UL, #tag > DIV, #headerTitle, #Option + ul > li, #Option + ul, #ListEdit, #TagInput, #AroundName
    {background-color:#191919!important}
  #MenuHome > UL, #MenuBlog > UL, #Option + ul
    {box-shadow:none!important}
  #Pickup, #Thumbnail9
    {background-image:none!important;background-color:#191919!important;border:1px solid #E70000!important;border-radius:0px!important;box-shadow:none!important}
  #TagCloud > A, .Navi > li > a, #HeaderNavi > li > a, #BlogInfo > h1 > a, #BlogDescription, #dashboard > ul > li > label, #headerTitle, .listUL > .row a, .oTitle, #Option + ul > li, #HomeInfo > h2 > a, #HomeInfo > div, #Thumbnail9 > div, #HomeInfo > h2, .permalink, #HeaderInput > input, #TagInput, #AroundName, .SimilarTumblr > div
    {color:white!important}
  #ListContent
    {background-color:#393939!important;border:1px solid black!important}
  #TagInput, #AroundName
    {border:1px solid rgb(231, 0, 0)!important; padding-left:4px!important}
  #HeaderInput > input, #SearchButton
    {border-top-left-radius:unset; border-bottom-left-radius:unset!important; border-top-right-radius:unset!important; border-bottom-right-radius:unset!important}
  #HeaderInput > input
    {background-color: rgba(25, 25, 25, 0.4)}
    
}

@-moz-document regexp('http://tmv\\.proto\\.jp/tmv2.0.*')

  
  {
  html, #tmvheader, #tmvMenu, body > div:nth-child(7)
    {background-color:#191919!important}
  #tmvheader > img:nth-child(2), #tmvMenu, body > div:nth-child(9), body > ul:nth-child(8), #tmvheader > div:nth-child(3) > div:nth-child(1), body > div:nth-child(6) > form:nth-child(1), #tmvheader > img:nth-child(4)
    {-webkit-filter: invert(90%) grayscale(100%); filter: invert(90%) grayscale(100%)}
    #searchBox
    {color:white!important}
  }
