css:
@-moz-document url(http://incredible.nomeata.de/) {
#taskbottombar,#taskdialog, #leftpane, #v-2, #header, .labels, .label, text, textarea, button{
background-color:#000;
fill:#fff;
color:#fff;
stroke-opacity:1.0;
}
rect, .body, tspan{
    fill:#000;
}
.block .body{
     fill:#000;
}
.block:hover .body {
    fill: #444;
}
#taskbottombar:hover {
    background-color:#333;
}
#taskdialog .inferencerule.attempted.solved{
  	background-color:#070;
}
#taskdialog .inferencerule.attempted{
      	background-color:#097;
}

.v-line{
fill:#fff;
}
#header{
	margin:0;
    padding:0;
}

g, path{
background-color:#000;
}
text{
fill:#fff;
}
#header{
	max-height:0px
}
}
