css:
@-moz-document url-prefix("https://www.google.com/_/chrome/newtab") {
#most-visited, #mngb, #prm-pt {
    display: none;
}

html {
    display: table-cell;
    vertical-align: middle;
    text-align: center;
}

body {
    min-height: 0;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
}

#lga {
    height: auto;
    margin-top: 0 !important;
}

#hplogo {
    padding-top: 0 !important;
    padding-bottom: 27px;
}

#f {
    padding-bottom: 75px;
}
}
