css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
.yt-lockup-meta-info{display: none !important;} /*счётчики на главной*/
.watch-view-count{display: none !important;} /*счётчик на просмотре*/
.yt-subscription-button-subscriber-count-branded-horizontal.yt-subscriber-count{display: none !important;} /*подписчики на просмотре*/
.like-button-renderer{display: none !important;} /*лайкидизлайки*/
.video-extras-sparkbars{display: none !important;} /*полоска лайков*/
.video-list .video-list-item .description, .video-list .video-list-item .stat{display: none !important;} /*счётчики справа*/
#watch7-sidebar-contents > div{display: none !important;} /*правая панель*/
.comments .comment-item .like-count.off, .comments .comment-item.liked .like-count.on{display: none !important;} /*комментарии и лайки*/
 
}
