css:
@-moz-document url(http://volnorez.com/lovim-volnu-vmeste) {

#wadsense_TopLine, #page_channel_sdesc + div{display:none !important;}

#wadmin, #sidebars, #fsheadercontainer,#wsocialcomments,#footer,#wsocial,.wbanner_ImageShow,.std_SubHeaderList,.wbanner_TextShow,.wcdesc_ShowFullCont, hr,hr+div,#wcdj_request_button{display:none !important;}
#container,#wcdesc {margin-top:0px !important;
box-shadow: 0px 0px #fff !important;
border:0px !important
}
html, body {
    margin: 0 !important;
    padding: 0 !important;
}
#container, .content-all,#main_wrapper, #content {
    width: 100% !important;
}
.channel_TopSecondLine {
    position: absolute;
    top: 20px;
    z-index: 9;
    right: 10px;
    border:none !important;
}
.channel_TopCont {
    font-family: Arial;
    height: 140px !important;
    position: relative;
    border:none !important;
}
#channel_chat{
    margin-left:-5px;
    width:100% !important;
    height: 400px !important;
}

#wcdesc .channel_desc_cont_collapsed, #chat_widget {
    height:auto;
}

#wcdesc {
    display: block;
    border-bottom: 1px solid #d1d1d1;
    width: 100%
}

.afisha_widget_container{
    position: absolute;
    top: 625px;
    left: 51% !important;
    width: 49% !important;
    overflow: hidden !important;
    background:white;
}
h2{display:none;}
#wcdesc .channel_desc_cont {
    width: 48% !important;
}
#sub-container {min-height:0;overflow: hidden;}

.channel_TopRight {
    height: 2em !important;
    margin: -2em !important;
    top: 22px !important;
    right: 0px !important;
    position: absolute !important;
}

.channel_RatingInfoListeners {
    margin-left: 24px !important;
    display: block !important;
    margin: 0 !important;
}

.channel_header{
 width: 90% !important;
    border:none !important;
}


.channel_Presenter {
    display: block !important;
}
}
