/***************************************************/
/* Overall */

.post-title {
    font-size: 24px !important;
}
/***************************************************/
/* Comments */

.commentlist > .comment {
    padding: 1em;
    margin-bottom: -0.5em;
    border: none;
}
.commentlist li.odd {
    background-color: #ddd !important;
}
li.comment:last-of-type {
    margin-bottom: 1em;
}
div.comment-author {
    font-weight: bold;
}
div.commentnumber {
    position: absolute;
    left: -2em;
}
.commentlist .yarr {
    visibility: hidden;
    position: absolute;
    bottom: 0.5em;
    right: 0.5em;
}
.commentlist li:hover .yarr {
    visibility: visible;
}
cite.fn span {
    /* Make editable comment bold like the others.
    Some elements remain a little off, but close enough. */
    
    font-weight: bold !important;
    font-size: 14px !important;
}
/***************************************************/
/* Comment form */

p[class^='comment-form'] {
    /* Pack the form closer. */
    
    padding: 0 !important;
    margin: -1em !important;
}
input:focus,
textarea:focus {
    /* I like input boxes with focus colors */
    
    background-color: #ffd !important;
}
/***************************************************/
/* Hide various useless things */

.av-special-heading {
    /* Section headings really aren't necessary */
    
    display: none !important;
}
a.small-preview {
    /* Thumbnails are huge and broken anyway */
    
    display: none !important;
}
span.image-overlay,
span.image-overlay-inside {
    display: none !important;
}
aside.sidebar_right {
    border-left: none !important;
}
main.content {
    border-right: none !important;
}
/***************************************************/
/* Whitespace fixes. */

main.template-page {
    padding: 0;
}
/* undo some enfold.js brain damage */

.content .entry-content-wrapper p a {
    display: inline !important;
}
/***************************************************/
/* Various Widgets */

#scroll-to-bottom-link {
    /* Shove it out of the way a bit more */
    
    margin-left: -2.5em;
}
#scroll-top-link {
    /* Doesn't need any treatment, just left here so 
    I remember the inconsistent naming */
}
}
