css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {
body { background: #000 !important; }
.newtab-cell:not([ignorehover]) .newtab-control:hover ~ .newtab-link, .newtab-cell:not([ignorehover]) .newtab-link:hover, .newtab-site[dragged] { border: 2px solid rgba(255,255,255,.2) !important; box-shadow: 0 0 6px 1px #002D59 !important; margin: -2px !important; }
.newtab-sponsored, .newtab-title { background: #000 !important; }
.newtab-title { border: 0 !important; border-top: 1px solid #222 !important; }
.newtab-sponsored, .newtab-title, .newtab-suggested { color: #AAA !important; }
#newtab-search-text { background: hsla(0,0%,10%,.9) padding-box !important; color: #CCC; }
.contentSearchSuggestionTable { background-color: hsla(0,0%,100%,.49) }
.contentSearchHeader { background-color: hsla(0, 0%, 97%, .7) }
}
