css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"){
  /*Show the "click me" cursor over the Autoplay button, rather than the normal arrow*/
  #autoplay-checkbox{cursor: pointer;}
  
  /*Disable the AutoPlay info button (we all know what it does anyway)*/
  .autoplay-info-icon{display: none;}
  
  /*Disable the footer of useless information*/
  #footer, #footer-container{display: none;}
  #body-container{padding-bottom: 0;}
  
  /*Disable ad at top of right sidebar of videos*/
  #watch7-sidebar-ads{display: none;}
  
  /*Disable ad at top of home page*/
  #feed-pyv-container{display: none;}
  
  /*Disable the Share button and box on videos*/
  button.pause-resume-autoplay:nth-child(2), #watch-action-panels{display: none;}
  
  /*Disable "Browse channels" and "Manage subscriptions" buttons in bottom of left sidebar*/
  #guide_builder-guide-item > a:nth-child(1){display: none;}
  #subscription_manager-guide-item > a:nth-child(1){display: none;}
  li.guide-section:nth-child(4){display: none;}
  
  /*Disable Youtube Red link in bottom of sidebar*/
  #unlimited-guide-item > a:nth-child(1){display: none;}
  
  /*Disable recommended channels*/
  .branded-page-v2-secondary-col{display: none;}
  
  /*Disable "Home" button in left sidebar (I click on the YT logo instead)*/
  #what_to_watch-guide-item > a:nth-child(1){display: none;}
  
  /*Disable "History" button in left sidebar*/
  #history-guide-item > a:nth-child(1){display: none;}
}
