css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ivlog.tv") {

.header {
    width: 90%;
    height: 40px;
}

.headcontainer {
    width: 99%;
    margin-left: 10%;
    margin-right: 10%;
}

.container {
    width: 80%;
    margin-left: 7%;
    margin-right: 7%;
    position: static;
}

#chat {
    overflow-y: visible;
    overflow-x: visible;
    word-wrap: normal;
    padding-top: 5px;
    font-size: 11px;
    position: static;
}

#chatForm {
    padding: 2px;
    width: 280px;
    height: 635px;
    float: left;
}

#messages {
    list-style: none;
    overflow-y: auto;
    overflow-x: visible;
    margin-left: 5px;
    padding: 0;
    word-wrap: break-word;
    max-height: 280px;
}

.heading {
    text-align: justify;
    background-color: PURPLE;
    border-radius: 10px;
}

#chatWrapper {
    box-shadow: 0px 4px 2px #828181 inset;
    height: 290px;
    overflow-y: hidden;
    overflow-x: hidden;
    width: 490px;
    background: #FFF;
    opacity: 0.9;
    border-radius: 10px;
    float: right;
}

#titleholder {
    width: 420px;
    height: 23px;
    float: right;
    background-color: #FFFFFF;
    border-radius: 10px;
    box-shadow: 0px 4px 2px #828181 inset;
    padding-top: 8px;
    font-size: 18px;
    overflow: visible;
}

.proCams {
    background-image: url(http://i674.photobucket.com/albums/vv102/Flirty473/lunapic_130314977121952_1.gif);
    width: 1200px;
    float: left;
    border-radius: 15px;
    box-shadow: 5px 5px 5px #000000;
    margin-bottom: 10px;
    position: relative;
    left: -95px;
    height: 158px;
}

.vcount {
    float: none;
    background-image: url(http://i674.photobucket.com/albums/vv102/Flirty473/lunapic_130314977121952_1.gif);
    ;
    border-radius: 0px 0px 10px 10px;
    text-align: center;
    width: 110px;
    font-size: 14px;
    color: #999999;
}

#chatList {
    height: 760px;
    width: 160px;
    background-image: url(http://i674.photobucket.com/albums/vv102/Flirty473/lunapic_130314977121952_1.gif);
    border-radius: 10px;
    position: absolute;
    right: 11px;
    top: 55px;
    padding: 7px;
}

#extrasList {
    height: 330px;
    overflow-y: auto;
    overflow-x: hidden;
    width: 160px;
    background-image: url(http://i674.photobucket.com/albums/vv102/Flirty473/lunapic_130314977121952_1.gif);
    border-radius: 10px;
    position: absolute;
    right: 11px;
    top: 500px;
    list-style: none outside none;
    padding: 7px;
    display: none;
}

#privateList {
    height: 330px;
    overflow-y: auto;
    overflow-x: hidden;
    width: 160px;
    background-image: url(http://i674.photobucket.com/albums/vv102/Flirty473/lunapic_130314977121952_1.gif);
    border-radius: 10px;
    position: absolute;
    right: 11px;
    top: 500px;
    list-style: none outside none;
    padding: 7px;
    display: none;
}
}
