css:
@-moz-document domain("earthsky.org") {
html body {
  background: #333;
  color: #bbb;
}

#main .container {
  background: #333;
}

h1, h2, h3, h4, h5, h6 {
  color: #ddd !important;
}

body .single-post-title,
body .single-post-title a {
  color: #ddd;
}

body .post blockquote p {
  color: #aaa;
}

body .post a,
body a,
body a:active,
body a:hover,
body a:focus {
  color: #007AE6;
}

.post_crumbs span.crumb_time {
  color: #999 !important;
}

div.wp-caption {
  background-color: #222;
}

.post {
  border-top-color: #666;
}

#featured_posts h4 a,
div.wp-caption p.wp-caption-text {
  color: #aaa !important;
}

.ti-title {
  background-color: #222 !important;
}

#secondaryNav li a {
  color: #ccc !important;
}

.post_intro .excerpt {
  background-color: #222 !important;
}
.post_intro .excerpt_border {
  background-color: #111 !important;
}
.post_intro .excerpt p {
  color: #bbb !important;
}

body ul#sharebar {
  background-color: #444 !important;
  border-color: #222 !important;
}

#featured_widget {
  background-color: #222 !important;
  border-bottom-color: #111;
}

body .widget a {
  color: #bbb !important;
}

#TB_title {
    background-color: #333 !important;
}

#TB_window {
    background-color: #222;
    color: #ddd;
    border-color: #222;
}

}

@-moz-document url-prefix("http://earthsky.wufoo.com/embed/"), url-prefix("https://earthsky.wufoo.com/embed/") {
.wufoo .info h2 {
    color: #ddd !important;
}


.wufoo .info div {
    color: #bbb !important;
}

.wufoo .info, .wufoo .paging-context {
    border-bottom-color: #555;
}

.wufoo label.desc, .wufoo legend.desc {
    color: #ccc !important;
}
}
