css:
@-moz-document domain(www.agent-stats.com) {
  #myheader {
  display: none;
}
body > .container > .row {
  float: left;
  position: absolute;
  top: 0;
  right: 0
}
body > .container > #mainGraph.row, body > .container > .separated.row {
  position: static;
  clear: left;
}
body > .container > div {
  width: 50%;
  float:left;
}
#mainGraph .highcharts-container {
  width: auto !important;
}
.col-md-12.scrollable {
  width: 50%;
  float: left;
}
.col-md-12.predictionList {
  width: 50%;
  float: left;
}
}
