css:
@-moz-document regexp("^.*//b.hatena.ne.jp/\\w+/.*(#.*)?") {
/*
 * None
*/
#logo, #help-pane, .right-container,
#page-title, #profile-actions,
#appstore-link, #follow-official-account, .recommend-content
{ display: none !important; }

/*
 * Layout
*/
.centerarticle-wrapper {
  display: flex;
}
.center-container {
  flex: 1;
  width: auto;
  max-width: none;
}

/*
 * Styling
*/
#header {
  border-bottom: #4B6B91;
}
.profile-image {
  opacity: 0.2;
}
.main-entry-list .entry-block {
  padding: 2em 0;
}
.main-entry-list .entry-comment {
  background: #F3F7FC;
  font-size: 70%;
}
.main-entry-list .entry-comment .profile-image,
.main-entry-list .entry-block .service-icon {
  opacity: 0.5;
}
.main-entry-list .entry-title a {
  font-size: 18px;
  font-weight: bold;
}
.main-entry-list .entry-title a:visited {
  color: #BBC5D3;
  font-weight: normal;
}
.centerarticle-reaction-comment,
.centerarticle-reaction-following {
  background: hsla(212, 60%, 97%, 1);
}

}
