css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soeren-hentzschel.at") {

    /***********/
    /* SCHRIFT */
    /***********/
    .snippet,
    .snippet > *,
    #sidebar .container,
    #sidebar H3,
    #sidebar .support-button,
    #comments H4,
    #comment-add H5
    {
      font-size:     1.4rem    !important;
    }

    .comments SPAN
    {
      font-size:     1.2rem    !important;
    }

    ARTICLE H1,
    ARTICLE H2,
    [property="headline"]
    {
      font-size:     1.8rem    !important;
    }


    /**********/
    /* BREITE */
    /**********/
    #content-wrapper
    {
      width:           70%     !important;
    }
    #content
    {
      width:          100%     !important;
    }

    /* Cookie-Nachricht */
    #storage-notice
    {
      display:        none     !important;
    }
}
