css:
@-moz-document url-prefix("http://www.douban.com/"), url-prefix("http://www.douban.com/subject/"),  url-prefix("http://www.douban.com/review/"){


/*1222更新 豆瓣日记*/

.note {
    font-family: microsoft yahei;
    margin-left: 23px;line-height: 1.92;}


/*1116更新*/
.td-subject a{font-size: 18px;}
#db-isay{width: 96%;
    margin: 2%;}
.usr-pic{margin-left:15px;}
.statuses-setting .hd .a_status_setting{right: 9px;
    top: -19px;
    font-size: 0.8em;}

.content .title p{line-height:1.8;}
.mod blockquote{line-height:1.4;}
.mod blockquote p{line-height:1.8;}
.explore-cate-mod li{font-size:14px;line-height:16px;}
.explore-cate-bd{margin-left: 57px;}
#expore-doulists{margin-left: 10%;}
.stream-items .status-item p,.stream-items .status-item a,.stream-items .status-item .source{font-size:12px;}
.channel-item{margin: 18px;}
.paginator .thispage {
    background-color: #645647;}
.wr{width: 96%;margin-left: 2%;}
.profile-entry .info a:link, .profile-entry .info a:visited, .profile-entry .info a:hover, .profile-entry .info a:active {
        color: #645647;
}



/*1116更新结束*/

  body{background:#fffcf6 url(http://static.smartisanos.cn/notes/asset/251447135278139/cloud_note_bg.jpg) repeat;}



.grid-16-8 .article {
    width: 806px;
background: #fffcf6;margin-left: -20px;
}

.topic-reply li {
    background: #fffcf6;
}


.olt {
    width: 96%;
    margin-left: 2%;}


a:link {
    color: #645647;}



.pro-banner img {
    width: 806px;}

body, td, th {
    font-size: 18px;
    line-height: 1.82;
font-family:Helvetica,'Microsoft Yahei',STHeiti-Beta Regular,方正中等线简体,ST-Heiti;
}


.olt .title {
    font-size: 18px;
}
.topic-content .topic-doc {
    width: 694px;}

.topic-doc {
    font-size: 18px;
line-height: 1.82;}
  

.topic-content .user-face, .topic-reply .user-face {
    margin: 19px;
}

#content{
    width: 1085px;}


.grid-16-8 .aside {
    width: 300px;

opacity:0.15;
}

.grid-16-8 .aside:hover {
    width: 300px;

opacity:1;-webkit-transition: 0.2s ease-out 0.1s;
    -moz-transition: 0.2s ease-out 0.1s;
    -o-transition: 0.2s ease-out 0.1s;
    transition: 0.2s ease-out 0.1s;
}
#content, .grid-16-8 {
    width: 1132px;}


.olt .td-reply {
    width: 91px;
}


.olt .td-group {
    width: 184px;
}


.olt .td-reply {
    color: #D8D8D8;
}

.olt .td-time{
    color: #D8D8D8;
}

a:visited {
    color: #D8D8D8;}

a:hover {
    background:none;color:#645647;font-weight:bold;-webkit-transition: 0.2s ease-out 0.1s;
    -moz-transition: 0.2s ease-out 0.1s;
    -o-transition: 0.2s ease-out 0.1s;
    transition: 0.2s ease-out 0.1s;}

.topic-content .topic-doc {
    float: left;

    margin-left: 8px;margin-top: 8px;
}

#db-usr-profile {
    height: auto;
    margin: 15px;
}

.sort{margin-left:15px;}


.topics h2 {
    text-indent: 8px;
    font-family: microsoft yahei;
}


}





@-moz-document url-prefix("http://book.douban.com/"),url-prefix("http://movie.douban.com/"){

html{background:#fffcf6 url(http://static.smartisanos.cn/notes/asset/251447135278139/cloud_note_bg.jpg) repeat;}

#content{background:#fffcf6}
#wrapper { width: 990px;}
.grid-16-8 .article {
    margin: 15px;}
.grid-16-8 .aside {
    margin-top: 15px;
    margin-right: 16px;}
#content h1{line-height: 2.1;
    margin-left: 15px;}
#slide {
    margin-left: 16px;}
#lists {
    margin-left: 15px;
}


}
