css:
@-moz-document domain("orzx.im") {
TD{padding:8px !important;width: 960px;}
body, form, ul, li, p, dl, dd, dt, h, td, th, h3,a{    font-size: 12pt;font-family: 微软雅黑 !important;}
.list,.wrap {width: 1260px;}
.dragTable .middle {line-height: 25px;}
.dragTable .TAG,.dragTable .more {margin:-12px -15px 0px !important;}
.dragTable .head .L,.dragTable .head .R ,.dragTable .TAG{background:none!important;}
.mainBox,#Copyright {margin:auto;} 
a:visited, .cse input.gsc-search-button, input.gsc-search-button:hover{color: #FF6A6A;}
input.gsc-search-button{
height: 36px;
min-width: 64px;
font-size: 12px;
margin-left: -12px;font-size: 15px; background : rgba(28,228,228,0.5) !important;border:2px solid rgba(255,256,17,0.7)!important;
}
#gsc-i-id1{height: 31px;}
input.gsc-input {font-size: 17px;background:none!important;}
table.gsc-search-box{width: 50%;}
.gsc-cursor-page{padding: 10px 14px;border:1px solid rgba(177,176,177,0.7)!important;border-radius:5px !important;font-size: 12px;}
.gsc-results .gsc-cursor-box .gsc-cursor-page:hover{border-width: 1px;
border-style: solid;
border-color: rgb(134, 185, 214);
background-color: rgb(245, 251, 255);}
.gsc-cursor-current-page{background : rgba(28,228,228,0.5) !important;}
}
