css:
@-moz-document domain("moviemagnet.net") {
    body, h2, h3, .play_time {
        font-weight: normal !important;
        color: black !important;
    }
    a { color: #006699; }
    td.td_torrent_quality { color: dimgray !important; }
    td.td_seed, .up-arrow { color: green !important; }
    td.td_leech { color: darkred !important; }
    #nav_header, #page_footer {
        background: rgba(0, 0, 0, 0.8);
    }
    #content_show {
        background: rgba(255, 255, 255, 0.5);
    }
}
