css:
@-moz-document domain("www.7cups.com") {
body {
    background-color: #5EAFAF !important;
}
.bio-expand, .chat-expand {
    background-image: url(https://cdn3.iconfinder.com/data/icons/google-material-design-icons/48/ic_keyboard_arrow_down_48px-32.png);
}
.bio-expand.open, .chat-expand.open {
    background-image: url(https://cdn3.iconfinder.com/data/icons/google-material-design-icons/48/ic_keyboard_arrow_up_48px-32.png);
}
.navbar {
    background: #198D8D !important;
    background-image: none;
    color: #99FFCC;
  }
#bottomStatus,
.bottomStatus {
    color: #99FFCC !important;
    background-color: #99FFCC !important;
}
#bottomStatus.heavyLoad {
    color: #99FFCC !important;
    background-color: #99FFCC !important;
    background: #99FFCC !important;
}
.btn-default,
.btn-primary {
    color: #000099 !important;
    background-color: #5CE6B8 !important;
}
.card .nav-link,
.active {
    color: #00537C !important;
    background-color: #C2F0FF !important;
}
.card {
    background-color: #C2F0FF !important;
}
.convHolder,
#actionButtons,
#actionButtons img {
    background-color: #C2F0FF !important;
}
.convHolder .note {
    color: #00537C !important;
}
.card-title {
    color: #00537C !important;
}
textarea {
    background-color: #C2F0FF !important;
    color: #000099 !important;
}
.chatBox .youWrap {
    background-color: #85D6FF;
}
.chatBox .meWrap {
    color: #000000 !important;
    background-color: #47D1FF;
}
.text-center, .details {
    color: #fff !important;
}
 #MessageScrollDivResize, #MessageScrollDiv
  {
    border:none !important;
  }
.youImg .userImage {
    border-radius: 50%;
}
.meImg .userScreenName {
    color: #000000; }
.userImage {
        border-radius: 20%;
}
.userScreenName {
    color: #000000;
}
.text-center, .details {
    color: #000 !important;
}
.btn-warning {
    color: #000099 !important;
    background-color: #4AB893 !important;
    border-color: #ffffff;
}
}
