css:
@-moz-document
regexp(".*_\/chrome\/newtab.*")
 {
html, body, .init .default-theme .des-mat{
    background: #454545 !important;
    color: white;
}
a, span{
    color: white !important;
}
.gb_2b{
    background: white;
    opacity: .72;
    border-radius: 50%;
}
.gb_2b .gb_Va .gb_le .gb_R{
    background: none;
}
#lga > img{
    background: rgba(0, 0, 0, .7);
    padding: 2em !important;
    padding-left: 5em !important;
    padding-right: 5em !important;
    border-radius: 5px;
    height: 72px !important;
    width: auto;
}
#logo-sub{
    display: none;
}
#fkbx{
    background: rgba(0, 0, 0, .7);
    border-radius: 5px;
}
#most-visited > #mv-tiles > .mv-tile{
    background: rgba(242,242,242, .3) !important;
    border-radius: 50% !important;
}
.mv-tile{
    color: white !important;
    border-radius: 50%;
}
}
