css:
@-moz-document domain(hackerforever.com) {
@font-face { font-family: 'Roboto';
    src: url("fonts/Roboto-Light.ttf") format("truetype")
}
body {
    background-image: url("http://gicnetwork.nl/images/Achtergrond%2520van%2520Windows%2520Photo%2520Viewer_09.jpg");
    background-size: auto;
    background-position: center;
    background-repeat: no-repeat;
}
h1,
h2,
h3 { 
    color: #FF6600;
}
#sidebar a#nickname { 
    color: #FF6600 !important;
}
img.icon { 
    padding: 4px;
    background-color: #FF6600;
}
.accordion>label { 
    background-color: #FF6600;
}
header nav>ul>li>ul>li a:hover { 
    background-color: #FF6600;
}
header nav>ul>li:hover>a+ul:before { 
    border-bottom: 15px solid #FF6600;
}
header nav>ul>li>ul { 
    background-color: #FF6600;
}
#main form.alt-design input,
#main form.alt-design select,
#main form.alt-design textarea,
#join form.alt-design input,
#join form.alt-design select,
#join form.alt-design textarea,
#login-wrap form.alt-design input,
#login-wrap form.alt-design select,
#login-wrap form.alt-design textarea { 
    background-color: #FF6600 !important;
    color: #fff !important;
}
#sidebar a#nickname:hover { 
    color: #FF6600 !important;
}
header nav>ul>li>ul { 
    display: none;
    background-color: #FF6600;
    color: #FF6600;
}
header nav>ul>li>a { 
    color: #FF6600;
}
#sidebar .col a { 
    color: #FF6600 !important;
    display: block;
    white-space: nowrap
}
}
