css:
@-moz-document domain("lichess.org") {
div.under_chat .watchers {
   border: 1px solid #ccc;
   height: 90px;
   box-sizing: border-box;
   overflow-y: auto;
   color: #999;
   width: calc(100% - 2px);
   display: inline-block !important;
   padding-top: 0px;
   padding-right: 5px;
   padding-bottom: 5px;
   padding-left: 5px;
}

body.dark div.under_chat .watchers {
   border: 1px solid #ccc;
   height: 90px;
   box-sizing: border-box;
   overflow-y: auto;
   width: calc(100% - 2px);
   border-color: #3d3d3d;
   background: #262626;
   display: inline-block !important;
   padding-top: 0px;
   padding-right: 5px;
   padding-bottom: 5px;
   padding-left: 5px;
}
}
