css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blackle.com") {
  div, a, p {
    background-color: transparent !important;
    border: none !important;;
  }
  body {
   background-color: #222;
    color: #AAA
  }
  #logo {
    display: none;
  }
  #s {
    margin: 0;
    width: 50%;
    min-width: 500px;
    margin: auto;
  }
  #wt {
    display: none;
  }
  #l, #hb, #lk {
    visibility: hidden;
  }
  .gsc-adBlock, .gsc-adBlockVertical {
    position: fixed !important;;
    top: -1000px;
  }
  .cse-branding-logo, .cse-branding-text {
    display: none !important;;
  }
  
  input {
    height: 35px !important;
    background-color: #EEE;
    background-position: 0% 50% !important;
    padding: 2px;
    box-sizing: border-box;
    border: none;
    color: #333;
  }
  
  .gs-text-box {
    display: none;
  }
  
  div.gsc-imageResult div.gsc-imageResult, div.gsc-imageResult-classic div.gsc-imageResult-classic {
    padding: 10px 2px;
  }
  
  .gsc-cursor {
    display: block;
    text-align: center;
    font-size: 2em;
  }
  .gsc-cursor-page {
    padding: 5px;
  }
}
