css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.extremetech.com") {

   /* main content background color */
  .off-canvas-wrap { background-color: #333333; }
  
  /* header text color */
  h1 { color: #b5b5b5; }
  h2, h3, h4, h5, h6 { color: black; }
  
  /* p tags text color */
  .post-container .post-content p { color: #b5b5b5; }
  
  /* byline color */
  .post-container .post-title .byline ul li span.vcard { color: black; }
  
  /* darkens the background of the bannerad up top */
  #bannerad { background-color: black; }
  
  /* the following darken the right sidebar */
  #toprightrail { background-color: black; }
  #widgetbox h3 {
    background-color: black;
    color: #b5b5b5;
  }
  .thumbnails-rr .trc_rbox_header { 
    background-image: none;
    background-color: black;
    color: #b5b5b5;    
  }
  
  /* darkens search and input fields unless selected */
  input:not([type]), input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], input[type="color"], textarea {
   background-color: black;  
  }
  
  /* makes backgrounds to story synopsis dark */
  .story-river ul li .deck { background-color: black; }
  
}
