css:
body {
	font-family: avenir;
	width: 900px;
	margin-left: auto;
	margin-right: auto;
}

h1 {
	text-align: center;
	margin-bottom: 25px;
}

pre {
	color: #4A4A4A;
	background: #E7F0F1;
	box-shadow: inset 0px 0px 3px 1px rgba(0,0,0,0.15);;
	padding: 10px;
	
}

a, a:visited {
	font-weight: 500;
	text-decoration: none;
	background: #E7F0F1;
	border: 1px solid #ABBDC2;
	border-radius: 5px;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	line-height: 200%;
	box-shadow: 0px 1px 5px 0px rgba(0,0,0,0.15);
}

a:hover {
	font-weight: 500;
	background: #CEDCDF;
	border: 1px solid #ABBDC2;
	border-radius: 5px;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	line-height: 200%;
	box-shadow: 0px 0px 5px 0px rgba(0,0,0,0.15);
}

a:active {
	font-weight: 500;
	color: #E7F0F1;
	background: #CEDCDF;
	border: 1px solid #ABBDC2;
	border-radius: 5px;
	padding-top: 3px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	line-height: 200%;
	box-shadow: 0px 0px 5px 0px rgba(0,0,0,0.15);
}
