css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.skype.com") {
  :root .swx .peoplePicker .separator p.strong {
    display: none;
    padding: 0;
  }
  :root .swx .peoplePicker .separator div.people {
    display: inline;
    padding: 0;
  }
  :root .swx .peoplePicker .separator .people ul {
    display: inline;
    padding: 0;
  }
  :root .swx .peoplePicker .separator .people .contactTile {
    display: inline;
    padding: 0;
    margin: 0;
  }
  :root .swx .row::before, :root .swx .row::after {
    content: "";
    display: none;
  }
  :root .swx .peoplePicker.all .separator {
    display: inline;
  }
  :root .swx .peoplePicker .separator {
    padding: 0;
  }
}
