css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  domain("skythewood.blogspot.sg"),
  domain("skythewood.blogspot.com") {

/*!========================================\\
|| Dark Skythewood Translations            ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for Skythewood      ||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/

  ::-moz-selection {
    background: #444444;
    color: #bbbbff
  }
  
  .content-outer, .content-fauxcolumn-outer, .region-inner {
    max-width: none !important;
  }
  
  div#comments {
    display: none;
  }
  
  div#Header1 {
    text-align: center;
  }
  
  div#header-inner {
    display: inline-block;
  }
  
  body, span, h1, h2, h3, h4, h5, h6, div.separator, u {
    color: #DDDDDD !important;
  }
  
  li {
    color: inherit !important;
  }
  
  div.content-inner, div.main-outer, span {
    background-image: none !important;
  }
  
  div.content-inner, div.post-footer {
    background-color: #000000 !important;
  }
  
  div.main-outer {
    background-color: #222222 !important;
  }
  
  a, ul, li, td, tr, table, div.thumb, span {
    background-color: inherit !important;
  }
  
  ul#icbabdrop li {
    background-color: #111111 !important;
  }
  
  ul#icbabdrop li {
    color: #aaffaa !important;
  }
  
  a, a > span {
    color: #aaaaff !important;
  }
  
  a:visited, a:visited > span {
    color: #ffaaff !important;
  }
  
  a.external, a.external > span {
    color: #aaffaa !important;
  }
  
  a.new, a.new > span {
    color: red !important;
  }
  
  footer {
    display: none;
  }
}
