css:
@-moz-document regexp("https*://community.arm.com/.*/blog.*") {
.jive-content-body .jive-blog-post-message .jive-rendered-content {
    max-width: 50em;    
    margin: 0 auto 0 auto;
}

.jive-content-body .jive-blog-post-message .jive-rendered-content p {
    max-width: 40em;
}

.jive-content-body .jive-blog-post-message .jive-rendered-content li {
    max-width: 40em;
}

.jive-content-body .jive-blog-post-message .jive-rendered-content img {
    max-width: 100%;    
}

.jive-content-body .jive-blog-post-message .jive-rendered-content dp-highlighter {
        max-width: 100%;
}

}
